                writer.WriteString("assetVersion", assetShortResponse.AssetVersion);

            if (assetShortResponse.VersionNameOption.IsSet)
                writer.WriteString("versionName", assetShortResponse.VersionName);

            if (assetShortResponse.VersionCodeOption.IsSet)
                writer.WriteNumber("versionCode", assetShortResponse.VersionCodeOption.Value!.Value);

            if (assetShortResponse.ReturningOption.IsSet)
                writer.WriteString("returning", assetShortResponse.Returning);
        }
    }
}
