            if (pathingResponse.SwcalibrationOption.IsSet)
            {
                writer.WritePropertyName("swcalibration");
                JsonSerializer.Serialize(writer, pathingResponse.Swcalibration, jsonSerializerOptions);
            }
            if (pathingResponse.CalibrationBearingsOption.IsSet)
                writer.WriteNumber("calibrationBearings", pathingResponse.CalibrationBearingsOption.Value!.Value);

            if (pathingResponse.NecalibrationOption.IsSet)
            {
                writer.WritePropertyName("necalibration");
                JsonSerializer.Serialize(writer, pathingResponse.Necalibration, jsonSerializerOptions);
            }
        }
    }
}
