                writer.WriteString("unit", productResponse.Unit);

            if (productResponse.StudioOption.IsSet)
                writer.WriteString("studio", productResponse.Studio);

            if (productResponse.ParentalRatingOption.IsSet)
                writer.WriteString("parentalRating", productResponse.ParentalRating);

            if (productResponse.PublishDateOption.IsSet)
                writer.WriteNumber("publishDate", productResponse.PublishDateOption.Value!.Value);

            if (productResponse.AvailabilityDateOption.IsSet)
                writer.WriteNumber("availabilityDate", productResponse.AvailabilityDateOption.Value!.Value);

            if (productResponse.SizeOption.IsSet)
            {
                writer.WritePropertyName("size");
                JsonSerializer.Serialize(writer, productResponse.Size, jsonSerializerOptions);
            }
        }
    }
}
