            if (trip.PickupTimeOption.IsSet)
                writer.WriteString("pickupTime", trip.PickupTimeOption.Value!.Value.ToString(PickupTimeFormat));

            if (trip.DropOffLocationOption.IsSet)
            {
                writer.WritePropertyName("dropOffLocation");
                JsonSerializer.Serialize(writer, trip.DropOffLocation, jsonSerializerOptions);
            }
            if (trip.DropOffTimeOption.IsSet)
                writer.WriteString("dropOffTime", trip.DropOffTimeOption.Value!.Value.ToString(DropOffTimeFormat));

            if (trip.ShipmentsOption.IsSet)
            {
                writer.WritePropertyName("shipments");
                JsonSerializer.Serialize(writer, trip.Shipments, jsonSerializerOptions);
            }
            if (trip.RecurrenceOption.IsSet)
            {
                writer.WritePropertyName("recurrence");
                JsonSerializer.Serialize(writer, trip.Recurrence, jsonSerializerOptions);
            }
            if (trip.VarTimeZoneOption.IsSet)
                writer.WriteString("timeZone", trip.VarTimeZone);

            if (trip.RouteOption.IsSet)
            {
                writer.WritePropertyName("route");
                JsonSerializer.Serialize(writer, trip.Route, jsonSerializerOptions);
            }
            var preferenceRawValue = Trip.PreferenceEnumToJsonValue(trip.PreferenceOption.Value!.Value);
            writer.WriteString("preference", preferenceRawValue);
            if (trip.NotificationsOption.IsSet)
                writer.WriteString("notifications", trip.Notifications);

            if (trip.MatchesOption.IsSet)
            {
                writer.WritePropertyName("matches");
                JsonSerializer.Serialize(writer, trip.Matches, jsonSerializerOptions);
            }
            if (trip.OwnerConnectionOption.IsSet)
            {
                writer.WritePropertyName("ownerConnection");
                JsonSerializer.Serialize(writer, trip.OwnerConnection, jsonSerializerOptions);
            }
            if (trip.OriginalIdOption.IsSet)
                writer.WriteNumber("originalId", trip.OriginalIdOption.Value!.Value);
        }
    }
